CREATE TABLE `inventory` (
  `description` varchar(250) DEFAULT NULL COMMENT 'Short description of the item, normally in the format: <manufacturer> <main-printers> <color> <type> [<yield>]\nEx: Dell 3760/3765 Black Toner [12k]',
  `printers` varchar(1000) DEFAULT NULL COMMENT 'Comma separated list of printers compatible with the item, not including manufacturer name.',
  `code` varchar(24) DEFAULT NULL COMMENT 'The item model code. This can mean different things for different manufacturers. Manufacturers also have their own formats.\nDell model codes usually have 5 characters (not to be confused with Dell part numbers which are 3 then 4 characters, with a - in the middle) --> GGCTW\n',
  `upc` varchar(24) NOT NULL COMMENT 'UPC code of the item. Ideally 12 characters. This is the primary key of the table, so no two items can have the same UPC code.',
  `stock` int DEFAULT NULL COMMENT 'Current number of the item held in inventory.',
  `part_num` varchar(15) DEFAULT NULL,
  `color` varchar(24) DEFAULT NULL,
  `brand` varchar(24) DEFAULT NULL,
  `yield` varchar(24) DEFAULT NULL,
  `type` varchar(16) DEFAULT NULL COMMENT 'Target stock number for items.',
  `comments` text COMMENT 'Catch-all column.',
  `linked_codes` varchar(120) DEFAULT NULL COMMENT 'A comma separted string of text that contains all codes that are linked with the item''s upc code.  The linked item''s will show up as the same item as the one they are linked to in the GUI to reduce confusion over multiple toner items of same type with different upc codes.',
  `needed` int DEFAULT NULL,
  `last_purchased` datetime DEFAULT NULL COMMENT 'Date item was last ordered.',
  PRIMARY KEY (`upc`),
  UNIQUE KEY `upc_UNIQUE` (`upc`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
